                try {
                    zks.getLeader().propose(request);
                } catch (XidRolloverException e) {
                    throw new RequestProcessorException(e.getMessage(), e);
                }
                //如果是事物消息的话，调用syncProcessor
                syncProcessor.processRequest(request);
            }
        }
